using System.Net;
using System.Net.Mail;
using System.Text.RegularExpressions;
using System.Threading.Tasks;
using PO_Api.Data.DTO;

public class EmailService
{
    private readonly SmtpClient _smtpClient;
    private readonly MailAddress _fromAddress;
    public EmailService(string host, int port, string username, string password, string fromEmail,string displayFromEmail)
    {
        _fromAddress = new MailAddress(fromEmail, displayFromEmail);

        _smtpClient = new SmtpClient(host, port)
        {
            EnableSsl = false,
            UseDefaultCredentials = false,
            Credentials = new NetworkCredential(username, password)
        };
    }

    public async Task SendEmailAsync(string to, string subject, EmailTemplateDTO body)
    {
        var Html = HtmlTemplate(body);
        var mail = new MailMessage 
        {
            From = _fromAddress,
            Subject = subject,
            Body = Html,
            IsBodyHtml = true
        };
        mail.To.Add(to);
        await _smtpClient.SendMailAsync(mail);
    }

